INSERT INTO word(name,def,category) VALUES ('인류철탑','한 방향에서 가해지는 합성된 풍압이나 수직하중에 의해 생기는 전가섭선의 불평균장력에 견딜 수 있도록 설계된 철탑.','전력')
INSERT INTO word(name,def,category) VALUES ('인산형연료전지','전해질로 인산(H3PO4)을 사용하는 연료전지이며, 작동온도는 160~250℃ 정도이다. 인산형 연료전지는 95% 이상의 진한 인산을 탄화규소(SiC) 매트릭스에 함침시킨 것을 전해질로 사용하며, 천연가스, 나프타, 액화석탄가스와 같은 화석연료나 메탄올 등의 연료를 개질기를 통하여 수소를 발생시켜 사용할 수 있으며 이에 함유되어 있는 이산화탄소나 미반응 탄화수소도 연료전지의 반응에는 영향을 미치지 않는 장점이 있다. 인산은 가격이 저렴하고 풍부하므로, 1960년대부터 주로 미국에서 개발이 진행되어 실용화된 시스템이며, 가장 먼저 상용화된 연료전지이다.','전력')
INSERT INTO word(name,def,category) VALUES ('인수점','탁송회사가 탁송의뢰자로부터 전력을 인수받는 점.','전력')
INSERT INTO word(name,def,category) VALUES ('인입','선로를 외부로부터 국내 또는 옥내로 연결하는 것.','전력')
INSERT INTO word(name,def,category) VALUES ('인입구','옥외 또는 옥측에서의 전로가 가옥의 외벽을 관통하는 부분을 말한다.','전력')
INSERT INTO word(name,def,category) VALUES ('인입구장치','인입구 이후의 전로에 설치하는 전원측으로부터 최초의 개폐기 및 과전류차단기를 합하여 말한다.- 인입구장치로서는 일반적으로 배선용차단기, 퓨즈 를 붙인 나이프스위치 또는 컷아웃스위치가 사용된다. 이들은 단순히 인입개폐기라 부르는 경우가 있다.- 분기회로수가 적을 경우에는 인입구장치의 개 폐기가 주개폐기, 분기개폐기 또는 조작개폐기를 겸하는 것도 있다.','전력')
INSERT INTO word(name,def,category) VALUES ('인입구장치','인입구 이후에 설치하는 전원측으로부터 최초의 개폐기및 과전류 차단기를 합하여 칭하는 것.','전력')
