INSERT INTO word(name,def,category) VALUES ('인입선 접속점','수용장소의 조영물 또는 보조지지물(완금, 애자 부착용금구 등)상의 가공인입선, 연접인입선 또는 지중인입선을 접속하는 전선접속점중 전원에 가장 가까운 곳을 말한다.','전력')
